property.value:
function init()
  geoff = root:findByName("geoff", false)
end


function onReceiveNotify(key, values)

  sendOSC("/"..self.parent.name.."/"..self.name.."/"..key, 1, {true})

end

function init()
holdTime = 1000 doubleTapTime = 300 past = 0 multi = "Tap"
end
function onValueChanged(key)
-- On Press. if key == "touch" and self.values.touch == true then
local now = getMillis()
if(now - past < doubleTapTime) then multi = "DoubleTap" else multi = "Tap" end past = getMillis()
-- On Release. elseif key == "touch" and self.values.touch == false then
self.parent:notify(multi, self.values.x) past = getMillis()
end end
function onPointer()
local now = getMillis()
if(now - past > holdTime) then multi = "Hold" self.values.x = 0 end end

